var script = document.createElement('script');
script.src = 'script.min.js';
document.head.appendChild(script);


let submitForm = ()=>{
    let username = document.getElementById("username").value
    let email = document.getElementById("email").value
    let password = document.getElementById("password").value
    let cPassword = document.getElementById("c-password").value


    if(username === ""){
        document.getElementById("usertxt").innerHTML = "Please Fill out username"
        document.getElementById("username").style.border = "1px solid red"   
    }
   
    else    if( email === ""){ 
        document.getElementById("emailtxt").innerHTML = "Please Fill out email"
        document.getElementById("email").style.border = "1px solid red"
    }
    else if(password === ""){
        document.getElementById("passtxt").innerHTML = "Please Fill out password"
        document.getElementById("password").style.border = "1px solid red"
      
    }

    else if(cPassword === ""){
        document.getElementById("cpasstxt").innerHTML = "Please Fill out c password"
        document.getElementById("c-password").style.border = "1px solid red"
        
    }

      else{
        let userData = {
            name : username,
            email: email,
            password : password,
            cPassword : cPassword
        }

        console.log(userData);
        

        username = document.getElementById("username").value = ""
        email = document.getElementById("email").value = ""
        password = document.getElementById("password").value = ""
        cPassword = document.getElementById("c-password").value = ""

        window.location.href = "protected.html"

    }

}




